import Card from "../Card/card.js";
import { useSelector } from "react-redux";
import { useState } from "react";

import "./cards.css";


function Cards(props) {

  const perros = useSelector((state) => state.dogs);
  const maxShow = 8
  const [initialFrame,setFrame] = useState(0)
  const lastFrame = initialFrame + maxShow
  const dogShow = perros.slice(initialFrame,lastFrame) //Aray con los perros que se van a renderizar
  console.log("perros es "  +perros)
  console.log("dogshow es "  +dogShow)
  function avPag(){
    setFrame(initialFrame+8)
  }
  function repag(){  setFrame(initialFrame-8)}
  return (
      <div className="ampa">
        {dogShow.map((perro) => (
          <Card
            key={perro.id}
            id={perro.id}
            name={perro.name}
            height={perro.height}
            width={perro.width}
            lifespan={perro.lifespan}
            image={perro.reference_image_id}/>))}
            <br></br>
            <div><button onClick={avPag}>Next</button>
            <button onClick={avPag}>Back</button></div>
        
      </div>
      )
        }
        
        export default Cards;
