// import validate from './Validation';
import React from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
export default function Form(props){

    const login = props.login
const navigate =useNavigate()

const [userData,SetUserData] = useState({email:"",password:""})
const [error,setErrors] = useState({})
function HandleOnChange (e){
    console.log(e.target.name)
    const nombre = e.target.name;
    SetUserData({...userData,[nombre]: e.target.value});
    console.log (userData)
    setErrors(validate({...userData,[nombre]: e.target.value}))
}

const handleSubmit = event => {
    event.preventDefault();
    props.login(userData);
  }


return (
<form
>
<label style={{color:"black",fontSize:"1em"}}>Nombre Raza : </label> <input style={{fontSize:"1em"}} name="name" onChange={HandleOnChange}></input>
<label style={{color:"black",fontSize:"1em"}} >    Altura Mínima: </label> <input name="maxHeight"  onChange={HandleOnChange}></input>
<label style={{color:"black",fontSize:"1em"}} >    Altura Máxima: </label> <input name="minHeight"  onChange={HandleOnChange}></input>
<label style={{color:"black",fontSize:"1em"}} >    Peso: </label> <input name="weight"  onChange={HandleOnChange}></input>
<label style={{color:"black",fontSize:"1em"}} >    Temperamentos: </label> <input name="temperaments"  onChange={HandleOnChange}></input>
<label style={{color:"black",fontSize:"1em"}} >    Años de vida: </label> <input name="spanLife"  onChange={HandleOnChange}></input>


<hr style={{margin:"15px"}}></hr>   


<button type="submit" style={{margin:"5px"}} onClick={handleSubmit}> Enviar </button>
<br></br>
<Link to="/nuevousuario"> <button  style={{margin:"5px"}} > Registrarse </button> </Link> 
</form>


)
// {/* <a1 style={{color:"red"}}>{error.email ? error.email: null} </a1>
// <a1 style={{color:"red"}}>{error.password ? error.password: null} </a1> */}
}
