import Card from "../Card/card.js";
import { useSelector } from "react-redux";
import { useState } from "react";

import "./cards.css";


function Cards(props) {

  const perros = useSelector((state) => state.dogs);
  const maxShow = 8
  const [initialFrame,setFrame] = useState(0)
  const [adeshabilitado,setdeshabilitado] = useState(true)

  const lastFrame = initialFrame + maxShow
  const dogShow = perros.slice(initialFrame,lastFrame) //Aray con los perros que se van a renderizar
  console.log("perros es "  +perros)
  console.log("dogshow es "  +dogShow)
  function avPag(){
    setFrame(initialFrame+8)
    setdeshabilitado(false)
  }
  function deshabilitado(){}
  function rePag(){
    setFrame(initialFrame-8)
    if (initialFrame<16) setdeshabilitado(true)
  }
  return (
      <div className="ampa">
        <div className="paginacion">
          <button className="button" disabled={adeshabilitado} onClick={rePag}>Back</button>
        <button className="button" onClick={avPag}>Next</button></div>
            <div/>

               <div className="cards-container">
        {dogShow.map((perro) => (
          <Card
            key={perro.id}
            id={perro.id}
            name={perro.name}
            height={perro.height}
            width={perro.width}
            lifespan={perro.lifespan}
            image={perro.reference_image_id}/>))}
            <br></br>
          
            </div>
      </div>
      )
        }
        
        export default Cards;
