import Card from "../Card/card.js";
import { useSelector } from "react-redux";
import { useState } from "react";
import { OrderDog } from "../Redux/Actions.js";

import "./cards.css";


function Cards(props) {

  const perros = useSelector((state) => state.dogs);
  const temps = useSelector((state) => state.temps);
  const maxShow = 8
  const [initialFrame,setFrame] = useState(0)
  const [adeshabilitado,setdeshabilitado] = useState(true)

  const lastFrame = initialFrame + maxShow
  const dogShow = perros.slice(initialFrame,lastFrame) //Aray con los perros que se van a renderizar
  console.log("perros es "  +perros)
  console.log("dogshow es "  +dogShow)
  function avPag(){
    setFrame(initialFrame+8)
    setdeshabilitado(false)
  }
  const handleOrder = (event) => {
    dispatch(OrderDog(event.target.value));
  };
  function rePag(){
    setFrame(initialFrame-8)
    if (initialFrame<16) setdeshabilitado(true)
  }
  return (
      <div className="ampa">
    
        <div className="paginacion">
          <button className="button" disabled={adeshabilitado} onClick={rePag}>Back</button>
        <button className="button" onClick={avPag}>Next</button></div>
        
            <div/>
            
          <select  name="Ordenado" onChange={handleOrder} >
            <option name="Orden" value="A">
              Ascendente
            </option>
            <option name="order" value="D">
              Descendente
            </option>
          </select>
          <select  name="Peso">
            <option name="Orden" value="A">
              Ascendente
            </option>
            <option name="order" value="D">
              Descendente
            </option>
          </select>
          <select  name="filter">
            <option value="All">All</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Genderless">Genderless</option>
            <option value="unknown">unknown</option>
          </select>
          <select  name="posteado">
            <option value="All">All</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Genderless">Genderless</option>
            <option value="unknown">unknown</option>
          </select>
               <div className="cards-container">
        {dogShow.map((perro) => (
          <Card
            key={perro.id}
            id={perro.id}
            name={perro.name}
            height={perro.height}
            width={perro.width}
            lifespan={perro.lifespan}
            image={perro.reference_image_id}/>))}
            <br></br>
          
            </div>
      </div>
      )
        }
        
        export default Cards;
